    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Log in{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <link rel=\"stylesheet\" href=\"{{ asset('assets/css/login.css') }}\">
{% endblock %}

{% block body %}
    <form method=\"post\">
        {% if error %}
            <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
        {% endif %}

        {% if app.user %}
            <div class=\"\">
                You are logged in as {{ app.user.userIdentifier }}, <a href=\"{{ path('app_logout') }}\">Logout</a>
            </div>
        {% endif %}

        <div class=\"login-container\">
            <h1 class=\"mb-4\">Login to Twitter</h1>

            <div class=\"form-group\">
                <label for=\"inputEmail\" class=\"sr-only mb-3\">Email</label>
                <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\"
                       autocomplete=\"email\" required autofocus>
            </div>
            <div>
                <label for=\"inputPassword\" class=\"sr-only\">Password</label>
                <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control mb-3\"
                       autocomplete=\"current-password\" required>
            </div>
            <input type=\"hidden\" name=\"_csrf_token\"
                   value=\"{{ csrf_token('authenticate') }}\"
            >
            <button class=\"new-btn\" type=\"submit\">
                Sign in
            </button>
            <div class=\"options\">
                <a href=\"#\">Forgot password?</a>
                <span> · </span>
                <a href={{ path('app_register') }}>Sign up for Twitter</a>
            </div>
        </div>

    </form>
{% endblock %}
", "login/index.html.twig", "/home/nicolas/Bureau/dev/symfony/my-twitter/templates/login/index.html.twig");
